function isParent(row) {
  parentMap = new java.util.HashMap();
  parentList = new java.util.ArrayList();
  var related = row.get('mmd_related_dataset');
  if (related != null) {
    parentList.add("true");
  } else {
    parentList.add("false");
  }
  parentMap.put('set', parentList);
  row.put('isParent', parentMap);
  return row;
}

