function createPins(row) {
  pinMap = new java.util.HashMap();
  pinList = new java.util.ArrayList();
  var north = row.get('mmd_geographic_extent_rectangle_north');
  var south = row.get('mmd_geographic_extent_rectangle_south');
  var east = row.get('mmd_geographic_extent_rectangle_east');
  var west = row.get('mmd_geographic_extent_rectangle_west');

  var lat = (south + north) / 2;
  var lon = (east + west) / 2;
  pinList.add(lat);
  pinList.add(lon);
  pinMap.put('set', lat+','+lon);
  row.put('locs_geo_extent', pinMap);
  return row;
}
function personnel(row) {
