  var fax = row.get('mmd_personnel_fax');
  var org = row.get('mmd_personnel_organisation');
  var addr = row.get('mmd_personnel_contact_address_address');
  var addr_city = row.get('mmd_personnel_contact_address_city');
  var addr_country = row.get('mmd_personnel_contact_address_country');
  var addr_postal = row.get('mmd_personnel_contact_address_postal_code');
  var addr_province = row.get('mmd_personnel_contact_address_province_or_state');

  if (role != null) {
    for (var i = 0; i < role.length; i++) {
      if (role[i].trim().toLowerCase() === "technical contact") {
        row.put('personnel_technical_role', 'Technical contact'); row.put('personnel_role', 'Technical contact');
      if( name != null) {  if( name[i] != null) { row.put('personnel_technical_name', name[i]);  row.put('personnel_name', name[i]); } }
      if( org != null) {    if( org[i] != null) { row.put('personnel_technical_organisation',  org[i]);  row.put('personnel_organisation',  org[i]); } }
      if( email != null) {    if( email[i] != null) { row.put('personnel_technical_email', email[i]); } }
