        if( fax != null) {  if( fax[i] != null) { row.put('personnel_investigator_fax', fax[i]); } }
        if( addr != null) {  if( addr[i] != null) { row.put('personnel_investigator_address', addr[i]); } }
        if( addr_city != null) {  if( addr_city[i] != null) { row.put('personnel_investigator_address_city', addr_city[i]); row.put('personnel_contact_address_city', addr_city[i]); } }
        if( addr_country != null) {  if( addr_country[i] != null) { row.put('personnel_investigator_address_country', addr_country[i]); row.put('personnel_contact_address_country', addr_country[i]); } }
        if( addr_postal != null) {  if( addr_postal[i] != null) { row.put('personnel_investigator_address_postal_code', addr_postal[i]); } }
        if( addr_postal != null) {  if( addr_province[i] != null) { row.put('personnel_investigator_address_province_or_state', addr_province[i]); row.put('personnel_contact_address_province_or_state', addr_province[i]); } }
      }
    else if (role[i].trim().toLowerCase() === "metadata author" || role[i].trim().toLowerCase() === "dif author") {
        row.put('personnel_metadata_author_role', 'Metadata author');  row.put('personnel_role','Metadata author');
        if( name != null) {  if( name[i] != null) { row.put('personnel_metadata_author_name', name[i]);  row.put('personnel_name', name[i]); } }
        if( org != null) {  if( org[i] != null) { row.put('personnel_metadata_author_organisation', org[i]);  row.put('personnel_organisation', org[i]); } }
        if( email != null) {  if( email[i] != null) { row.put('personnel_metadata_author_email', email[i]); } }
        if( phone != null) {  if( phone[i] != null) { row.put('personnel_metadata_author_phone',  phone[i]); } }
        if( fax != null) {  if( fax[i] != null) { row.put('personnel_metadata_author_fax', fax[i]); } }
        if( addr != null) {  if( addr[i] != null) { row.put('personnel_metadata_author_address', addr[i]); } }
        if( addr_city != null) {  if( addr_city[i] != null) { row.put('personnel_metadata_author_address_city',addr_city[i]); row.put('personnel_contact_address_city',addr_city[i]); } }
        if( addr_country != null) {  if( addr_country[i] != null) { row.put('personnel_metadata_author_address_country',addr_country[i]); row.put('personnel_contact_address_country',addr_country[i]); } }
        if( addr_postal != null) {  if( addr_postal[i] != null) { row.put('personnel_metadata_author_address_postal_code', addr_postal[i] ); } }
        if( addr_province != null) {  if( addr_province[i] != null) { row.put('personnel_metadata_author_address_province_or_state',addr_province[i]); row.put('personnel_contact_address_province_or_state',addr_province[i]); } }
      }
      else {
      row.put('personnel_role', role[i]);  row.put('personnel_role', role[i]);
      if( name != null) {  if( name[i] != null) { row.put('personnel_name', name[i]); } }
      if( org != null) {  if( org[i] != null) { row.put('personnel_organisation', org[i]); } }
      if( email != null) {  if( email[i] != null) { row.put('personnel_email', email[i]); } }
      if( phone != null) {  if( phone[i] != null) { row.put('personnel_phone', phone[i]); } }
      if( fax != null) {  if( fax[i] != null) { row.put('personnel_fax', fax[i] ); } }
      if( addr != null) {  if( addr[i] != null) { row.put('personnel_address',addr[i]); } }
      if( addr_city != null) {  if( addr_city[i] != null) { row.put('personnel_address_city',addr_city[i]); } }
      if( addr_country != null) {  if( addr_country[i] != null) { row.put('personnel_address_country',addr_country[i]);  } }
      if( addr_postal != null) {  if( addr_postal[i] != null) { row.put('personnel_address_postal_code',addr_postal[i]); } }
      if( addr_province != null) {  if( addr_province[i] != null) { row.put('personnel_address_province_or_state',addr_province[i]); } }
      }
    }
  }
  var dc_role = row.get("mmd_data_center_contact_role");
  var dc_name = row.get("mmd_data_center_contact_name");
  var dc_org = row.get("mmd_data_center_contact_organisation");
  if (dc_role != null) {
      if (dc_role.trim().toLowerCase() === "data center contact" || dc_role.trim().toLowerCase() === "datacenter") {
        row.put('personnel_datacenter_role','Data center contact');  row.put('personnel_role', 'Data center contact');
        if( dc_name != null) {  row.put('personnel_datacenter_name', dc_name);  row.put('personnel_name', dc_name); }
        if( dc_org != null) {  row.put('personnel_datacenter_organisation', dc_org);  row.put('personnel_organisation', dc_org); }
      }
  }

  return row;
}
